def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []

    # Assuming context contains large documents, we will chunk them
    for document in context:
        chunks = chunk_by_section(document, max_chunk_size=3000, overlap=20)

        # Create a task to extract revenue information from each chunk
        for chunk in chunks:
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract the total revenue for each fiscal year mentioned in this chunk.",
                advice="Look for sections that mention 'revenue' or 'total revenue' and note the fiscal year."
            ))

        # Create a task to extract capex information from each chunk
        for chunk in chunks:
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract the capital expenditures (capex) for each fiscal year mentioned in this chunk.",
                advice="Look for sections that mention 'capital expenditures', 'capex', or similar terms and note the fiscal year."
            ))

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    revenue_data = {}
    capex_data = {}

    for job in jobs:
        output = job.output
        if "revenue" in job.manifest.task.lower():
            if output.answer:
                # Parse the answer to extract year and revenue
                # Assuming the answer format is "FY2017: $X, FY2018: $Y, FY2019: $Z"
                for line in output.answer.split(","):
                    year, revenue = line.split(":")
                    revenue_data[year.strip()] = float(revenue.strip().replace("$", "").replace(",", ""))

        elif "capex" in job.manifest.task.lower():
            if output.answer:
                # Parse the answer to extract year and capex
                # Assuming the answer format is "FY2017: $A, FY2018: $B, FY2019: $C"
                for line in output.answer.split(","):
                    year, capex = line.split(":")
                    capex_data[year.strip()] = float(capex.strip().replace("$", "").replace(",", ""))

    # Calculate capex as a percentage of revenue for each year
    capex_percentage = {}
    for year in revenue_data:
        if year in capex_data:
            capex_percentage[year] = (capex_data[year] / revenue_data[year]) * 100

    # Calculate the 3-year average
    if capex_percentage:
        average_percentage = sum(capex_percentage.values()) / len(capex_percentage)
        return f"The FY2017 - FY2019 3-year average of capex as a percentage of revenue is {average_percentage:.1f}%."
    else:
        return "Insufficient data to calculate the 3-year average of capex as a percentage of revenue."
