def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []
    task_description = "Extract the total revenue for FY2019 and FY2020 from this chunk. If available, provide the fiscal year and the corresponding revenue amount."

    for document in context:
        chunks = chunk_by_section(document, max_chunk_size=3000, overlap=20)
        for chunk in chunks:
            job_manifest = JobManifest(
                chunk=chunk,
                task=task_description,
                advice="Focus on sections that mention financial figures, especially those labeled as 'Total Revenue' or 'Net Revenue'.",
                task_id=1
            )
            job_manifests.append(job_manifest)

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    revenue_data = {}

    for job in jobs:
        output = job.output
        if output.answer:
            # Extract fiscal year and revenue from the answer
            lines = output.answer.split('\n')
            for line in lines:
                if "FY2019" in line or "FY2020" in line:
                    parts = line.split(':')
                    if len(parts) == 2:
                        year = parts[0].strip()
                        revenue = parts[1].strip()
                        revenue_data[year] = revenue

    # Aggregate the results
    result_str = "Extracted Revenue Data:\n"
    for year, revenue in revenue_data.items():
        result_str += f"{year}: {revenue}\n"

    return result_str
