    for document in context:
        chunks = chunk_by_section(document, max_chunk_size=3000, overlap=20)
        for chunk in chunks:
            job_manifest = JobManifest(
                chunk=chunk,
                task="Extract the operating income for FY2015 and FY2016 from this chunk.",
                advice="Look for sections labeled 'Income Statement' or 'Operating Income'.",
            )
            job_manifests.append(job_manifest)

    return job_manifests


def transform_outputs(
    jobs: List[Job],
) -> str:
    operating_incomes = {}

    for job in jobs:
        output = job.output
        if output.answer:
            # Extract the fiscal year and operating income from the answer
            # Assuming the answer format is something like "FY2015: $X, FY2016: $Y"
            parts = output.answer.split(',')
            for part in parts:
                year, income = part.split(':')
                year = year.strip()
                income = income.strip()
                operating_incomes[year] = income

    # Now we have a dictionary of operating incomes for FY2015 and FY2016
    fy2015_income = float(operating_incomes.get('FY2015', 0))
    fy2016_income = float(operating_incomes.get('FY2016', 0))

    # Calculate the year-over-year change
    if fy2015_income != 0:
        yoy_change = ((fy2016_income - fy2015_income) / fy2015_income) * 100
        yoy_change_str = f"The year-over-year change in unadjusted operating income from FY2015 to FY2016 is {yoy_change:.1f}%."
    else:
        yoy_change_str = "Unable to calculate year-over-year change due to missing or zero FY2015 income."

    return yoy_change_str
