    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []

    # Assuming context contains large documents, we will chunk them
    for document in context:
        chunks = chunk_by_section(document, max_chunk_size=3000, overlap=20)

        # Create jobs to extract FY2022 net income from each chunk
        for chunk in chunks:
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract the FY2022 net income from this chunk.",
                advice="Look for terms like 'net income', 'profit', or 'earnings' related to FY2022."
            ))

        # Create jobs to extract total assets for FY2021 and FY2022 from each chunk
        for chunk in chunks:
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract the total assets for FY2021 and FY2022 from this chunk.",
                advice="Look for balance sheet or statement of financial position sections mentioning total assets for FY2021 and FY2022."
            ))

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    net_income_outputs = []
    total_assets_outputs = []

    for job in jobs:
        if "net income" in job.manifest.task:
            if job.output.answer:
                net_income_outputs.append((job.output.answer, job.output.citation))
        elif "total assets" in job.manifest.task:
            if job.output.answer:
                total_assets_outputs.append((job.output.answer, job.output.citation))

    # Aggregate the results
    aggregated_result = "Net Income Outputs:\n"
    for answer, citation in net_income_outputs:
        aggregated_result += f"Answer: {answer}, Citation: {citation}\n"

    aggregated_result += "\nTotal Assets Outputs:\n"
    for answer, citation in total_assets_outputs:
        aggregated_result += f"Answer: {answer}, Citation: {citation}\n"

    return aggregated_result
