def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []

    # Assuming context contains large documents, we will chunk them
    for document in context:
        chunks = chunk_by_section(document, max_chunk_size=3000, overlap=20)

        for chunk in chunks:
            # Create a job to extract the quick ratio components for FY2023
            job_manifest_2023 = JobManifest(
                chunk=chunk,
                task="Extract the current assets, inventories, and current liabilities for FY2023.",
                advice="Focus on balance sheet data and ensure the fiscal year is 2023."
            )
            job_manifests.append(job_manifest_2023)

            # Create a job to extract the quick ratio components for FY2022
