def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []

    # Assuming context contains large documents, we need to chunk them
    for document in context:
        chunks = chunk_by_section(document, max_chunk_size=3000, overlap=20)

        # Create jobs to extract net income attributable to shareholders
        for chunk in chunks:
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract the net income attributable to shareholders for FY2022.",
                advice="Look for sections related to the income statement or financial summary.",
                task_id=1
            ))

        # Create jobs to extract total cash dividends paid
        for chunk in chunks:
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract the total cash dividends paid for FY2022.",
                advice="Look for sections related to the cash flow statement or dividend information.",
                task_id=2
            ))

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    net_income_results = []
    dividends_results = []

    for job in jobs:
        if job.manifest.task_id == 1:
            if job.output.answer:
                net_income_results.append((job.output.answer, job.output.citation))
        elif job.manifest.task_id == 2:
            if job.output.answer:
                dividends_results.append((job.output.answer, job.output.citation))

    # Aggregate results
    aggregated_str = "Net Income Attributable to Shareholders Results:\n"
    for answer, citation in net_income_results:
        aggregated_str += f"Answer: {answer}, Citation: {citation}\n"

    aggregated_str += "\nTotal Cash Dividends Paid Results:\n"
    for answer, citation in dividends_results:
        aggregated_str += f"Answer: {answer}, Citation: {citation}\n"

    return aggregated_str
