def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []

    # Assuming each document is large, we will chunk it into smaller sections
    # for more manageable processing. We will use a chunk size of 3000 characters.
    for doc in context:
        chunks = chunk_by_section(doc, max_chunk_size=3000, overlap=20)

        # Create a task to extract relevant financial metrics for each chunk
        task = "Extract any mention of financial metrics such as net interest margin, return on assets, return on equity, and efficiency ratio."
        advice = "Focus on identifying and extracting financial metrics relevant to a financial institution like JPMorgan Chase."

        for chunk in chunks:
            job_manifest = JobManifest(
                chunk=chunk,
                task=task,
                advice=advice
            )
            job_manifests.append(job_manifest)

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    # Initialize a dictionary to aggregate outputs by task_id and chunk_id
    aggregated_outputs = {}

    for job in jobs:
        task_id = job.manifest.task_id
        chunk_id = job.manifest.chunk_id

        # Initialize the entry if it doesn't exist
        if task_id not in aggregated_outputs:
            aggregated_outputs[task_id] = {}

        if chunk_id not in aggregated_outputs[task_id]:
            aggregated_outputs[task_id][chunk_id] = {
                "answers": [],
                "explanations": [],
                "citations": []
            }

        # Collect the outputs
        output = job.output
        if output.answer:
            aggregated_outputs[task_id][chunk_id]["answers"].append(output.answer)
        if output.explanation:
            aggregated_outputs[task_id][chunk_id]["explanations"].append(output.explanation)
        if output.citation:
            aggregated_outputs[task_id][chunk_id]["citations"].append(output.citation)

    # Create a comprehensive string from the aggregated outputs
    result = []
    for task_id, chunks in aggregated_outputs.items():
        for chunk_id, data in chunks.items():
            result.append(f"Task ID: {task_id}, Chunk ID: {chunk_id}")
            result.append("Answers: " + "; ".join(data["answers"]))
            result.append("Explanations: " + "; ".join(data["explanations"]))
            result.append("Citations: " + "; ".join(data["citations"]))
            result.append("\n")

    return "\n".join(result)
