def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []

    # Assuming context contains large documents, we will chunk them
    for document in context:
        chunks = chunk_by_section(document, max_chunk_size=3000, overlap=20)

        # Create jobs to extract cash from operations from the cash flow statement
        for chunk in chunks:
            if "cash flow statement" in chunk.lower():
                job_manifests.append(JobManifest(
                    chunk=chunk,
                    task="Extract the 'cash from operations' figure for FY2017.",
                    advice="Look for sections that detail cash flow from operations specifically for FY2017.",
                    task_id=1
                ))

        # Create jobs to extract total current liabilities from the balance sheet
        for chunk in chunks:
            if "balance sheet" in chunk.lower():
                job_manifests.append(JobManifest(
                    chunk=chunk,
                    task="Extract the 'total current liabilities' figure for FY2017.",
                    advice="Look for sections that detail current liabilities specifically for FY2017.",
                    task_id=2
                ))

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    cash_from_operations = None
    total_current_liabilities = None

    # Aggregate outputs by task_id
    for job in jobs:
        if job.manifest.task_id == 1:
            if job.output.answer:
                cash_from_operations = job.output.answer
        elif job.manifest.task_id == 2:
            if job.output.answer:
                total_current_liabilities = job.output.answer

    # Prepare the aggregated string for further inspection
    result = "Aggregated Financial Data for FY2017:\n"
    if cash_from_operations:
        result += f"Cash from Operations: {cash_from_operations}\n"
    else:
        result += "Cash from Operations: Not found\n"

    if total_current_liabilities:
        result += f"Total Current Liabilities: {total_current_liabilities}\n"
    else:
        result += "Total Current Liabilities: Not found\n"

    return result
