def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []

    # Assuming each document is large, we will chunk it into smaller sections
    # for more manageable processing. We will use a chunk size of 3000 characters.
    for doc in context:
        chunks = chunk_by_section(doc, max_chunk_size=3000, overlap=20)

        # Create a task to extract information about capital expenditures
        for chunk in chunks:
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract information about capital expenditures, including any figures or descriptions.",
                advice="Focus on sections that discuss financial investments, property, plant, and equipment.",
                task_id=1
            ))

        # Create a task to extract information about depreciation
        for chunk in chunks:
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract information about depreciation, including any figures or descriptions.",
                advice="Look for sections that mention depreciation expenses or amortization.",
                task_id=2
            ))

        # Create a task to extract information about the asset base
        for chunk in chunks:
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract information about the asset base, including total assets and fixed assets.",
                advice="Focus on balance sheet sections or any summary of assets.",
                task_id=3
            ))

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    capital_expenditures = []
    depreciation_info = []
    asset_base_info = []

    for job in jobs:
        if job.manifest.task_id == 1:
            if job.output.answer:
                capital_expenditures.append(f"Chunk {job.manifest.chunk_id}: {job.output.answer}")
        elif job.manifest.task_id == 2:
            if job.output.answer:
                depreciation_info.append(f"Chunk {job.manifest.chunk_id}: {job.output.answer}")
        elif job.manifest.task_id == 3:
            if job.output.answer:
                asset_base_info.append(f"Chunk {job.manifest.chunk_id}: {job.output.answer}")

    aggregated_output = (
        "Capital Expenditures Information:\n" + "\n".join(capital_expenditures) + "\n\n" +
        "Depreciation Information:\n" + "\n".join(depreciation_info) + "\n\n" +
        "Asset Base Information:\n" + "\n".join(asset_base_info)
    )

    return aggregated_output
