def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []

    # Assuming each document is large, we will chunk it into smaller sections
    # for more manageable processing.
    for document in context:
        chunks = chunk_by_section(document, max_chunk_size=3000, overlap=20)

        # Create a task to extract capex information from the statement of cash flows
        for chunk in chunks:
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract the capital expenditures (capex) figures from the statement of cash flows.",
                advice="Focus on identifying any numbers or tables that indicate capital expenditures for FY2018, FY2019, and FY2020.",
                task_id=1
            ))

        # Create a task to extract revenue information from the statement of income
        for chunk in chunks:
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract the total revenue figures from the statement of income.",
                advice="Look for any numbers or tables that indicate total revenue for FY2018, FY2019, and FY2020.",
                task_id=2
            ))

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    capex_data = {}
    revenue_data = {}

    # Process each job output
    for job in jobs:
        output = job.output
        if output.answer:
            if job.manifest.task_id == 1:
                # Collect capex data
                capex_data[job.manifest.chunk_id] = output.answer
            elif job.manifest.task_id == 2:
                # Collect revenue data
                revenue_data[job.manifest.chunk_id] = output.answer

    # Aggregate the results
    aggregated_results = "Capex Data:\n"
    for chunk_id, answer in capex_data.items():
        aggregated_results += f"Chunk {chunk_id}: {answer}\n"

    aggregated_results += "\nRevenue Data:\n"
    for chunk_id, answer in revenue_data.items():
        aggregated_results += f"Chunk {chunk_id}: {answer}\n"

    return aggregated_results
