def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []

    # Assuming context contains large documents, we will chunk them
    for document in context:
        chunks = chunk_by_section(document, max_chunk_size=3000, overlap=20)

        for chunk in chunks:
            # Create a job to extract operating income from the income statement
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract the operating income for FY2021 from the income statement.",
                advice="Look for sections labeled 'Income Statement' or similar.",
                task_id=1
            ))

            # Create a job to extract depreciation and amortization from the cash flow statement
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract the depreciation and amortization expenses for FY2021 from the cash flow statement.",
                advice="Look for sections labeled 'Cash Flow Statement' or similar.",
                task_id=2
            ))

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    operating_income = None
    depreciation_amortization = None

    for job in jobs:
        if job.manifest.task_id == 1 and job.output.answer:
            # Extract operating income
            if operating_income is None:
                operating_income = job.output.answer
            else:
                # If multiple answers, decide on a strategy (e.g., take the first non-empty)
                pass

        elif job.manifest.task_id == 2 and job.output.answer:
            # Extract depreciation and amortization
            if depreciation_amortization is None:
                depreciation_amortization = job.output.answer
            else:
                # If multiple answers, decide on a strategy (e.g., take the first non-empty)
                pass

    # Aggregate the results
    result = "Operating Income: {}\nDepreciation and Amortization: {}\n".format(
        operating_income if operating_income else "Not found",
        depreciation_amortization if depreciation_amortization else "Not found"
    )

    return result
