def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []

    # Assuming each document is large, we will chunk it into smaller sections
    for document in context:
        chunks = chunk_by_section(document, max_chunk_size=3000, overlap=20)

        for chunk in chunks:
            job_manifest = JobManifest(
                chunk=chunk,
                task="Extract the cash from operations and capital expenditures (capex) for FY2020. Provide the figures in USD millions if available.",
                advice="Focus on the statement of cash flows section. Look for specific figures related to cash from operations and capex for the fiscal year 2020."
            )
            job_manifests.append(job_manifest)

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    cash_from_operations = []
    capex = []

    for job in jobs:
        output = job.output
        if output.answer:
            if "cash from operations" in output.answer.lower():
                cash_from_operations.append(output.answer)
            elif "capex" in output.answer.lower() or "capital expenditures" in output.answer.lower():
                capex.append(output.answer)

    aggregated_result = "Cash from Operations for FY2020:\n"
    aggregated_result += "\n".join(cash_from_operations)
    aggregated_result += "\n\nCapital Expenditures (Capex) for FY2020:\n"
    aggregated_result += "\n".join(capex)

    return aggregated_result
