def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []

    # Assuming context contains large documents, we will chunk them
    for document in context:
        chunks = chunk_by_section(document, max_chunk_size=3000, overlap=20)

        for chunk in chunks:
            # Create a job to extract capital expenditure information
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract information about capital expenditures (CapEx) for FY 2022.",
                advice="Focus on any numerical data or statements related to capital expenditures.",
                task_id=1
            ))

            # Create a job to extract information about total assets and PP&E
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract information about total assets and property, plant, and equipment (PP&E) for FY 2022.",
                advice="Look for balance sheet data or any mention of assets and PP&E.",
                task_id=2
            ))

            # Create a job to extract information about depreciation and amortization expenses
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract information about depreciation and amortization expenses for FY 2022.",
                advice="Identify any expenses related to depreciation and amortization.",
                task_id=3
            ))

    return job_manifests


def transform_outputs(
    jobs: List[Job],
) -> str:
    capex_info = []
    assets_info = []
    depreciation_info = []

    for job in jobs:
        if job.manifest.task_id == 1:
            if job.output.answer:
                capex_info.append(f"CapEx Info: {job.output.answer} (Citation: {job.output.citation})")
        elif job.manifest.task_id == 2:
            if job.output.answer:
                assets_info.append(f"Assets Info: {job.output.answer} (Citation: {job.output.citation})")
        elif job.manifest.task_id == 3:
            if job.output.answer:
                depreciation_info.append(f"Depreciation Info: {job.output.answer} (Citation: {job.output.citation})")

    aggregated_info = "\n".join(capex_info + assets_info + depreciation_info)

    return f"Aggregated Financial Information for FY 2022:\n{aggregated_info}"
