def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []
    # Assuming each document is large, we will chunk it into smaller sections
    for doc in context:
        chunks = chunk_by_section(doc, max_chunk_size=3000, overlap=20)
        for chunk in chunks:
            job_manifest = JobManifest(
                chunk=chunk,
                task="Extract the total debt figures for the fiscal years 2021 and 2022 from this chunk.",
                advice="Focus on balance sheet sections and any notes that mention debt figures.",
            )
            job_manifests.append(job_manifest)
    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    debt_2021 = []
    debt_2022 = []

    for job in jobs:
        if job.output.answer:
            if "2021" in job.output.answer:
                debt_2021.append(job.output.answer)
            if "2022" in job.output.answer:
                debt_2022.append(job.output.answer)

    aggregated_output = "Debt Figures for 2021:\n"
    aggregated_output += "\n".join(debt_2021)
    aggregated_output += "\n\nDebt Figures for 2022:\n"
    aggregated_output += "\n".join(debt_2022)

    return aggregated_output
```

