def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []
    task_id = 1  # Unique identifier for the task

    # Chunk the document by section
    for doc in context:
        chunks = chunk_by_section(doc)

        # Create a task for each chunk to extract total debt for 2021
        for chunk in chunks:
            task_2021 = "Extract the total debt reported for the 2021 fiscal period."
            job_manifest_2021 = JobManifest(
                chunk=chunk,
                task=task_2021,
                advice="Look for sections in the balance sheet or notes that mention total debt for 2021."
            )
            job_manifests.append(job_manifest_2021)

        # Create a task for each chunk to extract total debt for 2022
        for chunk in chunks:
            task_2022 = "Extract the total debt reported for the 2022 fiscal period."
            job_manifest_2022 = JobManifest(
                chunk=chunk,
                task=task_2022,
                advice="Look for sections in the balance sheet or notes that mention total debt for 2022."
            )
            job_manifests.append(job_manifest_2022)

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    def filter_fn(job):
        return job.output.answer is not None and job.output.answer.strip() != ""

    # Filter jobs
    for job in jobs:
        job.include = filter_fn(job)

    # Aggregate and filter jobs
    tasks = {}
    for job in jobs:
        task_id = job.manifest.task_id
        chunk_id = job.manifest.chunk_id

        if task_id not in tasks:
            tasks[task_id] = {
                "task_id": task_id,
                "task": job.manifest.task,
                "chunks": {},
            }

        if chunk_id not in tasks[task_id]["chunks"]:
            tasks[task_id]["chunks"][chunk_id] = []

        tasks[task_id]["chunks"][chunk_id].append(job)

    # Build the aggregated string
    aggregated_str = ""
    for task_id, task_info in tasks.items():
        aggregated_str += f"## Task (task_id=`{task_id}`): {task_info['task']}\n\n"

        for chunk_id, chunk_jobs in task_info["chunks"].items():
            filtered_jobs = [j for j in chunk_jobs if j.include]

            aggregated_str += f"### Chunk # {chunk_id}\n"
            if filtered_jobs:
                for idx, job in enumerate(filtered_jobs, start=1):
                    aggregated_str += f"   -- Job {idx} (job_id=`{job.manifest.job_id}`):\n"
                    aggregated_str += f"   Answer: {job.output.answer}\n"
                    aggregated_str += f"   Explanation: {job.output.explanation}\n"
                    if job.output.citation:
                        aggregated_str += f"   Citation: {job.output.citation}\n"
                    aggregated_str += "\n"
            else:
                aggregated_str += "   No jobs returned successfully for this chunk.\n"

        aggregated_str += "\n-----------------------\n\n"

    return aggregated_str
```

