def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []

    # Assuming context contains large documents, we will chunk them
    for document in context:
        chunks = chunk_by_section(document, max_chunk_size=3000, overlap=20)

        # Create a task to extract unadjusted operating income
        for chunk in chunks:
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract the unadjusted operating income for FY2018, FY2019, and FY2020.",
                advice="Look for line items related to operating income in the P&L statement.",
                task_id=1
            ))

        # Create a task to extract depreciation and amortization
        for chunk in chunks:
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract the depreciation and amortization figures for FY2018, FY2019, and FY2020.",
                advice="Look for line items related to depreciation and amortization in the cash flow statement.",
                task_id=2
            ))

        # Create a task to extract total revenue
        for chunk in chunks:
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract the total revenue for FY2018, FY2019, and FY2020.",
                advice="Look for line items related to total revenue in the P&L statement.",
                task_id=3
            ))

    return job_manifests


def transform_outputs(
    jobs: List[Job],
) -> str:
    operating_income = {}
    depreciation_amortization = {}
    total_revenue = {}

    for job in jobs:
        output = job.output
        if job.manifest.task_id == 1:
            if output.answer:
                # Parse the answer to extract operating income for each year
                # Assuming the answer is structured in a way that can be parsed
                # Example: "FY2018: 1000, FY2019: 1100, FY2020: 1200"
                for line in output.answer.split(','):
                    year, value = line.split(':')
                    operating_income[year.strip()] = float(value.strip())

        elif job.manifest.task_id == 2:
            if output.answer:
                # Parse the answer to extract depreciation and amortization for each year
                for line in output.answer.split(','):
                    year, value = line.split(':')
                    depreciation_amortization[year.strip()] = float(value.strip())

        elif job.manifest.task_id == 3:
            if output.answer:
                # Parse the answer to extract total revenue for each year
                for line in output.answer.split(','):
                    year, value = line.split(':')
                    total_revenue[year.strip()] = float(value.strip())

    # Aggregate the results into a single string
    result = "Aggregated Financial Data:\n"
    for year in ['FY2018', 'FY2019', 'FY2020']:
        if year in operating_income and year in depreciation_amortization and year in total_revenue:
            ebitda = operating_income[year] + depreciation_amortization[year]
            ebitda_margin = (ebitda / total_revenue[year]) * 100
            result += f"{year}: EBITDA Margin = {ebitda_margin:.1f}%\n"

    return result
```

