```python
def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []
    task_id = 1  # Unique identifier for the task

    # Chunk the document by sections to ensure we capture relevant financial data
    for doc in context:
        chunks = chunk_by_section(doc, max_chunk_size=3000, overlap=20)

        for chunk in chunks:
            # Create a task for extracting revenue and COGS for each fiscal year
            task_revenue = "Extract the total revenue for FY2016, FY2017, and FY2018."
            task_cogs = "Extract the cost of goods sold (COGS) for FY2016, FY2017, and FY2018."

            job_manifest_revenue = JobManifest(
                chunk=chunk,
                task=task_revenue,
                advice="Look for sections in the income statement that mention total revenue for each fiscal year."
            )
            job_manifest_cogs = JobManifest(
                chunk=chunk,
                task=task_cogs,
                advice="Look for sections in the income statement that mention cost of goods sold for each fiscal year."
            )

            job_manifests.append(job_manifest_revenue)
            job_manifests.append(job_manifest_cogs)

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    def filter_fn(job):
        return job.output.answer is not None and job.output.answer.strip() != ""

    # Filter jobs
    for job in jobs:
        job.include = filter_fn(job)

    # Aggregate and filter jobs
    tasks = {}
    for job in jobs:
        task_id = job.manifest.task_id
        chunk_id = job.manifest.chunk_id

        if task_id not in tasks:
            tasks[task_id] = {
                "task_id": task_id,
                "task": job.manifest.task,
                "chunks": {},
            }

