def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []

    # Assuming context contains large documents, we will chunk them
    for doc in context:
        chunks = chunk_by_section(doc, max_chunk_size=3000, overlap=20)

        for chunk in chunks:
            job_manifest = JobManifest(
                chunk=chunk,
                task="Extract the total revenue figures for FY2016 and FY2017 from this chunk.",
                advice="Look for line items labeled 'Total Revenue' or 'Net Sales' and note the fiscal year."
            )
            job_manifests.append(job_manifest)

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    revenue_data = {}

    for job in jobs:
        output = job.output
        if output.answer:
            # Extract fiscal year and revenue from the answer
            lines = output.answer.split('\n')
            for line in lines:
                if 'FY2016' in line or 'FY2017' in line:
                    parts = line.split(':')
                    if len(parts) == 2:
                        year = parts[0].strip()
                        revenue = parts[1].strip()
                        revenue_data[year] = revenue

    # Aggregate the results
    aggregated_result = "Extracted Revenue Data:\n"
    for year, revenue in revenue_data.items():
        aggregated_result += f"{year}: {revenue}\n"

    return aggregated_result
```

