def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []

    # Assuming each document is large, we will chunk it into smaller sections
    for doc in context:
        chunks = chunk_by_section(doc, max_chunk_size=3000, overlap=20)

        for chunk in chunks:
            job_manifest = JobManifest(
                chunk=chunk,
                task="Extract the total revenue and cost of goods sold (COGS) for FY2016, FY2017, and FY2018.",
                advice="Focus on identifying the specific figures for revenue and COGS for each fiscal year mentioned.",
            )
            job_manifests.append(job_manifest)

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    # Initialize dictionaries to store extracted data
    revenue_data = {}
    cogs_data = {}

    # Process each job output
    for job in jobs:
        output = job.output
        if output.answer:
            # Parse the answer to extract revenue and COGS data
            lines = output.answer.split('\n')
            for line in lines:
                if "FY2016" in line:
                    if "revenue" in line.lower():
                        revenue_data['FY2016'] = float(line.split()[-1])
                    elif "cogs" in line.lower():
                        cogs_data['FY2016'] = float(line.split()[-1])
                elif "FY2017" in line:
                    if "revenue" in line.lower():
                        revenue_data['FY2017'] = float(line.split()[-1])
                    elif "cogs" in line.lower():
                        cogs_data['FY2017'] = float(line.split()[-1])
                elif "FY2018" in line:
                    if "revenue" in line.lower():
                        revenue_data['FY2018'] = float(line.split()[-1])
                    elif "cogs" in line.lower():
                        cogs_data['FY2018'] = float(line.split()[-1])

    # Calculate COGS as a percentage of revenue for each year
    percentages = []
    for year in ['FY2016', 'FY2017', 'FY2018']:
        if year in revenue_data and year in cogs_data:
            percentage = (cogs_data[year] / revenue_data[year]) * 100
            percentages.append(percentage)

    # Calculate the average percentage
    if percentages:
        average_percentage = sum(percentages) / len(percentages)
        result = f"The three-year average of COGS as a percentage of revenue from FY2016 to FY2018 is {average_percentage:.1f}%."
    else:
        result = "Insufficient data to calculate the average COGS as a percentage of revenue."

    return result
