def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []

    # Chunk the documents by section
    chunks = []
    for doc in context:
        chunks.extend(chunk_by_section(doc, max_chunk_size=3000, overlap=20))

    # Define tasks
    task_id_cogs = 1
    task_id_inventory = 2

    # Create tasks for each chunk
    for chunk in chunks:
        # Task to extract FY2021 COGS
        job_manifests.append(JobManifest(
            chunk=chunk,
            task="Extract the FY2021 Cost of Goods Sold (COGS) from the income statement.",
            advice="Look for the section in the income statement that lists COGS for FY2021.",
            task_id=task_id_cogs
        ))

        # Task to extract FY2020 and FY2021 inventory values
        job_manifests.append(JobManifest(
            chunk=chunk,
            task="Extract the inventory values for FY2020 and FY2021 from the statement of financial position.",
            advice="Look for the inventory line items in the balance sheet for both FY2020 and FY2021.",
            task_id=task_id_inventory
        ))

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    def filter_fn(job):
        return job.output.answer is not None and job.output.answer.strip() != ""

    # Filter jobs
    for job in jobs:
        job.include = filter_fn(job)

    # Aggregate and filter jobs
    tasks = {}
    for job in jobs:
        task_id = job.manifest.task_id
        chunk_id = job.manifest.chunk_id

        if task_id not in tasks:
            tasks[task_id] = {
                "task_id": task_id,
                "task": job.manifest.task,
                "chunks": {},
            }

        if chunk_id not in tasks[task_id]["chunks"]:
            tasks[task_id]["chunks"][chunk_id] = []

        tasks[task_id]["chunks"][chunk_id].append(job)

    # Build the aggregated string
    aggregated_str = ""
    for task_id, task_info in tasks.items():
        aggregated_str += f"## Task (task_id=`{task_id}`): {task_info['task']}\n\n"

        for chunk_id, chunk_jobs in task_info["chunks"].items():
            filtered_jobs = [j for j in chunk_jobs if j.include]

            aggregated_str += f"### Chunk # {chunk_id}\n"
            if filtered_jobs:
                for idx, job in enumerate(filtered_jobs, start=1):
                    aggregated_str += f"   -- Job {idx} (job_id=`{job.manifest.job_id}`):\n"
                    aggregated_str += f"   Answer: {job.output.answer}\n"
                    aggregated_str += f"   Explanation: {job.output.explanation}\n"
                    if job.output.citation:
                        aggregated_str += f"   Citation: {job.output.citation}\n"
                    aggregated_str += "\n"
            else:
                aggregated_str += "   No jobs returned successfully for this chunk.\n\n"

        aggregated_str += "\n-----------------------\n\n"

    return aggregated_str
