def prepare_jobs(
    context: List[str],
    prev_job_manifests: Optional[List[JobManifest]] = None,
    prev_job_outputs: Optional[List[JobOutput]] = None,
) -> List[JobManifest]:
    job_manifests = []

    # Assuming context contains large documents, we will chunk them
    for document in context:
        chunks = chunk_by_section(document, max_chunk_size=3000, overlap=20)

        # Create tasks to extract relevant financial data from each chunk
        for chunk in chunks:
            # Task to extract accounts payable for FY2017 and FY2018
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract accounts payable figures for FY2017 and FY2018.",
                advice="Look for accounts payable data in the statement of financial position."
            ))

            # Task to extract COGS for FY2018
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract cost of goods sold (COGS) for FY2018.",
                advice="Look for COGS data in the profit and loss statement."
            ))

            # Task to extract inventory levels for FY2017 and FY2018
            job_manifests.append(JobManifest(
                chunk=chunk,
                task="Extract inventory levels for FY2017 and FY2018.",
                advice="Look for inventory data in the statement of financial position."
            ))

    return job_manifests

def transform_outputs(
    jobs: List[Job],
) -> str:
    accounts_payable_data = []
    cogs_data = []
    inventory_data = []

    for job in jobs:
        if "accounts payable" in job.manifest.task:
            if job.output.answer:
                accounts_payable_data.append((job.output.answer, job.output.citation))
        elif "COGS" in job.manifest.task:
            if job.output.answer:
                cogs_data.append((job.output.answer, job.output.citation))
        elif "inventory levels" in job.manifest.task:
            if job.output.answer:
                inventory_data.append((job.output.answer, job.output.citation))

    # Aggregate the extracted data
    aggregated_data = "Accounts Payable Data:\n"
    for data, citation in accounts_payable_data:
        aggregated_data += f"Answer: {data}, Citation: {citation}\n"

    aggregated_data += "\nCOGS Data:\n"
    for data, citation in cogs_data:
        aggregated_data += f"Answer: {data}, Citation: {citation}\n"

    aggregated_data += "\nInventory Data:\n"
    for data, citation in inventory_data:
        aggregated_data += f"Answer: {data}, Citation: {citation}\n"

    return aggregated_data
```

